targets.expr:
sum(
  kube_pod_container_resource_requests_memory_bytes{namespace="sisense", node=~"$Node"}
* on(namespace,pod)
  group_left(workload) mixin_pod_workload{namespace="sisense"}
) by (workload)

sum(
    container_memory_working_set_bytes{namespace="sisense", container!="",node=~"$Node",service=~"$Service"}
  * on(namespace,pod)
    group_left(workload) mixin_pod_workload{namespace="sisense"}
) by (workload)
/sum(
  kube_pod_container_resource_requests_memory_bytes{namespace="sisense",node=~"$Node"}
* on(namespace,pod)
  group_left(workload) mixin_pod_workload{namespace="sisense"}
) by (workload)

sum(
